                                cogsOwnerPay: 0,
                                cogsContractors: 0,
                                cogsMaterials: 0,
                                cogsCommissions: 0,
                                cogsOther: 0,
                                cogsTotal: 0,
                                payroll: 0,
                                ownerBasePay: 0,
                                benefits: 0,
                                insurance: 0,
                                professionalFees: 0,
                                subcontractors: 0,
                                rent: 0,
                                taxLicense: 0,
                                phoneComm: 0,
                                infrastructure: 0,
                                autoTravel: 0,
                                salesExpense: 0,
                                marketing: 0,
                                trainingCert: 0,
                                mealsEntertainment: 0,
                                interestExpense: 0,
                                depreciationAmortization: 0,
                                otherExpense: 0,
                                nonOperatingIncome: 0,
                                extraordinaryItems: 0,
                                cash: 0,
                                ar: 0,
                                inventory: 0,
                                otherCA: 0,
                                tca: 0,
                                fixedAssets: 0,
                                otherAssets: 0,
                                totalAssets: 0,
                                ap: 0,
                                otherCL: 0,
                                tcl: 0,
                                ltd: 0,
                                totalLiab: 0,
                                totalEquity: 0,
                                totalLAndE: 0
