                        <div style={{ margin: '8px 0 4px', fontSize: '14px', fontWeight: '600', color: '#475569' }}>Current Assets</div>
                        {balanceData.some(p => p.cash > 0) && <Row label="Cash & Cash Equivalents" values={balanceData.map(p => p.cash)} indent={20} />}
